import React, { Component, useState, useEffect } from "react";
import { useAuthState } from "react-firebase-hooks/auth";
import { useHistory } from "react-router";
import { auth, db,app,CurrentUser } from "../firebase";

import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

import Fab from '@mui/material/Fab';
import AddIcon from '@material-ui/icons/Add';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';

import {
Button,
TextField,
Grid,
Paper,
Link,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

import { Line } from "react-chartjs-2";

const data = {
  labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
  datasets: [
    {
      tension: 0.2,
      label: "First dataset",
      data: [33, 53, 85, 41, 44, 65],
      fill: true,
      backgroundColor: "rgba(75,192,192,0.2)",
      borderColor: "rgba(75,192,192,1)"
    },
    {
      label: "Second dataset",
      data: [33, 25, 35, 51, 54, 76],
      fill: false,
      borderColor: "#742774"
    },
  ]
};
const fabStyle = {
  position: 'absolute',
  bottom: 16,
  right: 16,
  color: 'common.white',
  bgcolor: '#FBBD14',
  '&:hover': {
    bgcolor: '#C99200',
  },
};

const test_array = [];

function Overview() {
  const [user, loading, error] = useAuthState(auth);
  const [name, setName] = useState("");
  const history = useHistory();
 
  const [transactionType_, setTransactionType_] = useState("");
  const [transactionName_, setTransactionName_] = useState("");
  const [price_, setPrice_] = useState("");

  const handleChangeTransactionType_ = (event) => {
    setTransactionType_(event.target.value);
  };
  const handleChangeTransactionName_ = (event) => {
    setTransactionName_(event.target.value);
  };
  const handleChangePrice_ = (event) => {
    setPrice_(event.target.value);
  };


  const [forgot, setForgot] = React.useState(false);
  const handleForgotOpen = () => {
    setForgot(true);
  };

  const handleForgotClose = () => {
    setForgot(false);
  };

  const [test, setTest] = useState({
    transactions: [],
    money: 0,

    transactionName: '',
    transactionType: '',
    price: '',
    currentUID: auth.currentUser.uid

  });



  const addNewTransaction = () =>
  {
    

    if(transactionName_ && transactionType_ && price_){

      const BackUpState = test_array;
      BackUpState.push({
          id: BackUpState.length + 1,
          name: transactionName_,
          type: transactionType_,
          price: price_,
          user_id: user?.uid
      });

      fire.database().ref('Transactions/' + user?.uid).push({
        id: BackUpState.length + 1,
        name: transactionName_,
        type: transactionType_,
        price: price_,
        user_id: user?.uid
    }).then((data) => {
        //success callback
        console.log('success callback');
    }).catch((error)=>{
        //error callback
        console.log('error ' , error)
    });
    }


      
  };
  useEffect(() => {
    const fetchUserName = async () => {
      try {
        const query = await db
          .collection("users")
          .where("uid", "==", user?.uid)
          .get();
        const data = await query.docs[0].data();
        setName(data.name);
      } catch (err) {
        console.error(err);
        alert("An error occured while fetching user data");
      }
    };
    if (loading) return;
    if (!user) return history.replace("/");
    
    fetchUserName();
  }, [user, loading,
    history]);
  return (
    <div>
      
      <p class="tag-next-test">
        Welcome, {name}!
      </p>
      <p class="tag-next-balance">
        Your Balance: PHP 500,000
      </p>
      <div class="row">
      <div class="column"><Line data={data} /></div>
      <div class="column"><Line data={data} /></div>
      </div> 

      <div class="transaction-block">
      <Fab sx={fabStyle} aria-label="add" onClick={handleForgotOpen}>
        <AddIcon />
      </Fab>
      <Dialog open={forgot} onClose={handleForgotClose} fullWidth>
      <DialogTitle>Transaction</DialogTitle>
        <DialogContent>
          <Grid container direction="column" spacing={2} >
          <Grid item>
          <TextField
            autoFocus
            margin="dense"
            id="t-name"
            label="Transaction Name"
            type="text"
            fullWidth
            variant="standard"
            onChange={(e) => setTransactionName_(e.target.value)}
          />
          </Grid>
          <Grid item>
          <TextField
            margin="dense"
            id="Price "
            label="Price"
            type="text"
            fullWidth
            variant="standard"
            onChange={(e) => setPrice_(e.target.value)}
          />
          </Grid>
          <Grid item>
          <FormControl fullWidth>
          <InputLabel id="type-label">Transaction type</InputLabel>  
           <Select
          labelId="type-label"
          id="type-label"
          label="Transaction Type"
          autoWidth
          value={transactionType_}
          onChange={handleChangeTransactionType_}
          >
          <MenuItem value={"income"} autoWidth>Income</MenuItem>
          <MenuItem value={"expense"} autoWidth>Expense</MenuItem>
        </Select>
        </FormControl>
          </Grid>
        </Grid>
        </DialogContent>
          <DialogActions>
            <Button onClick={handleForgotClose}>Cancel</Button>
            <Button onClick={addNewTransaction}>Add Transaction</Button>
          </DialogActions>
      </Dialog>
      </div>
    </div>
  );
}

export default Overview;
