import React, { useEffect, useState } from "react";
import { useAuthState } from "react-firebase-hooks/auth";
import { useHistory } from "react-router";
import { auth, db } from "../firebase";
import { makeStyles } from "@material-ui/core/styles";
import { Bar } from "react-chartjs-2";


import { Line } from "react-chartjs-2";

const data = {
  labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
  datasets: [
    {
      tension: 0.2,
      label: "First dataset",
      data: [33, 53, 85, 41, 44, 65],
      fill: true,
      backgroundColor: "rgba(75,192,192,0.2)",
      borderColor: "rgba(75,192,192,1)"
    },
    {
      label: "Second dataset",
      data: [33, 25, 35, 51, 54, 76],
      fill: false,
      borderColor: "#742774"
    },
  ]
};

function Overview() {
  const [user, loading, error] = useAuthState(auth);
  const [name, setName] = useState("");
  const history = useHistory();
 
  

  useEffect(() => {
    const fetchUserName = async () => {
      try {
        const query = await db
          .collection("users")
          .where("uid", "==", user?.uid)
          .get();
        const data = await query.docs[0].data();
        setName(data.name);
      } catch (err) {
        console.error(err);
        alert("An error occured while fetching user data");
      }
    };
    if (loading) return;
    if (!user) return history.replace("/");
    
    fetchUserName();
  }, [user, loading,
    history]);
  return (
    <div>
      
      <p class="tag-next-test">
        Welcome, {name}!
      </p>
      <p class="tag-next-balance">
        Your Balance: PHP 500,000
      </p>
      <div class="row">
      <div class="column"><Line data={data} /></div>
      <div class="column"><Line data={data} /></div>
      </div> 

      <div class="transaction-block">
      <Grid container direction="column" spacing={2} >
              
              <Grid item>
                <TextField
                  id="outlined-email"
                  label="Email"
                  variant="outlined"
                  className = {classes.customTextBox}
                />
              </Grid>
              <Grid item>
                <TextField
                  id="outlined-pw"
                  label="Password"
                  type="password"
                  variant="outlined"
                  className = {classes.customTextBox}
                />
              </Grid>
              <Grid item>
              <Button
              variant="contained"              
              type="submit"
              className="button-block"
              style = {{background: '#FBBD14',
                        color:'#FFFFFF',
                        fontFamily:'Montserrat SemiBold',
                        fontSize: '16px'}}
              >Login</Button>

              </Grid>
              <Grid item>
              <Link onClick={handleForgotOpen} className="fpw">
              Forgot Password?
              </Link>

              <Dialog open={forgot} onClose={handleForgotClose}>
                <DialogTitle>Forgot Password</DialogTitle>
                  <DialogContent>
                    <DialogContentText>
                        Submit the email you signed up with to reset your password.
                    </DialogContentText>
                    <TextField
                      autoFocus
                      margin="dense"
                      id="name"
                      label="Email Address"
                      type="email"
                      fullWidth
                      variant="standard"
                    />
                  </DialogContent>
                    <DialogActions>
                      <Button onClick={handleForgotClose}>Close</Button>
                      <Button onClick={() => sendPasswordResetEmail(email)}>Reset Password</Button>
                    </DialogActions>
                </Dialog>
              </Grid>
            </Grid>
      </div>
    </div>
  );
}

export default Overview;
