import React, { useEffect, useState } from "react";
import { useAuthState } from "react-firebase-hooks/auth";
import { useHistory } from "react-router";
import { auth, db } from "../firebase";

import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

import Fab from '@mui/material/Fab';
import AddIcon from '@material-ui/icons/Add';

import {
Button,
TextField,
Grid,
Paper,
Link,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

import { Line } from "react-chartjs-2";

const data = {
  labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
  datasets: [
    {
      tension: 0.2,
      label: "First dataset",
      data: [33, 53, 85, 41, 44, 65],
      fill: true,
      backgroundColor: "rgba(75,192,192,0.2)",
      borderColor: "rgba(75,192,192,1)"
    },
    {
      label: "Second dataset",
      data: [33, 25, 35, 51, 54, 76],
      fill: false,
      borderColor: "#742774"
    },
  ]
};
const fabStyle = {
  position: 'absolute',
  bottom: 16,
  right: 16,
};


function Overview() {
  const [user, loading, error] = useAuthState(auth);
  const [name, setName] = useState("");
  const history = useHistory();
 
  const [forgot, setForgot] = React.useState(false);
  const handleForgotOpen = () => {
    setForgot(true);
  };

  const handleForgotClose = () => {
    setForgot(false);
  };

  useEffect(() => {
    const fetchUserName = async () => {
      try {
        const query = await db
          .collection("users")
          .where("uid", "==", user?.uid)
          .get();
        const data = await query.docs[0].data();
        setName(data.name);
      } catch (err) {
        console.error(err);
        alert("An error occured while fetching user data");
      }
    };
    if (loading) return;
    if (!user) return history.replace("/");
    
    fetchUserName();
  }, [user, loading,
    history]);
  return (
    <div>
      
      <p class="tag-next-test">
        Welcome, {name}!
      </p>
      <p class="tag-next-balance">
        Your Balance: PHP 500,000
      </p>
      <div class="row">
      <div class="column"><Line data={data} /></div>
      <div class="column"><Line data={data} /></div>
      </div> 

      <div class="transaction-block">
      <Fab sx={fabStyle} aria-label="add" onClick={handleForgotOpen}>
        <AddIcon />
      </Fab>
      <Dialog open={forgot} onClose={handleForgotClose}>
      <DialogTitle>Forgot Password</DialogTitle>
        <DialogContent>
          <DialogContentText>
              Submit the email you signed up with to reset your password.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email Address"
            type="email"
            fullWidth
            variant="standard"
          />
        </DialogContent>
          <DialogActions>
            <Button onClick={handleForgotClose}>Close</Button>
            <Button onClick={{handleForgotClose}}>Reset Password</Button>
          </DialogActions>
      </Dialog>
      </div>
    </div>
  );
}

export default Overview;
